import { Routes, Route, useLocation } from "react-router-dom";
import EnglishMainPage from "./pages/EnglishMainPage";
import UkrainianMainPage from "./pages/UkrainianMainPage";
import ProNas from "./pages/ProNas";
import { useTransition, animated } from "react-spring";

function App() {
  const location = useLocation();
  const transitions = useTransition(location, (location) => location.pathname, {
    from: {
      opacity: 0,
    },
    enter: {
      opacity: 1,
    },
    leave: {
      opacity: 1,
    },
  });
  return (
    <>
      {transitions.map(({ item, props, key }) => (
        <animated.div key={key} style={props}>
          <Routes path={item}>
            <Route exact path="" element={<UkrainianMainPage />} />
            <Route exact path="/english" element={<EnglishMainPage />} />
            <Route exact path="/proNas" element={<ProNas />} />
          </Routes>
        </animated.div>
      ))}
    </>
  );
}

export default App;
