import { useSpring, animated } from "react-spring";
import UkrainianMainPageTitle from "./UkrainianMainPageTitle";
function MainPageAnimation(props){
    const styles = useSpring({
      to: { opacity: 1, marginTop:0},
      from: { opacity: 0,marginTop:-900},
    });
  return (
    <>
        <animated.div style={styles}><UkrainianMainPageTitle/></animated.div>
    </>
  );
};
export default MainPageAnimation;
