import { Routes, Route, useLocation } from "react-router-dom";
import EnglishMainPage from "./pages/EnglishMainPage";
import UkrainianMainPage from "./pages/UkrainianMainPage";
import ProNas from "./pages/ProNas";
import { useTransition, animated } from "react-spring";

function App() {
  const location = useLocation();
  const transitions = useTransition(location, {
    from: { opacity: 0, transform: "translateY(100%)" },
    enter: { opacity: 1, transform: "translateY(0%)" },
    leave: { opacity: 0, transform: "translateY(-100%)" },
  });
  return transitions((props, item) => (
    <div style={{ position: "relative"}}>
      <animated.div style={props}>
        <div style={{ position: "absolute", width: "100%" }}>
          <Routes location={item}>
            <Route exact path="" element={<UkrainianMainPage />} />
            <Route exact path="/english" element={<EnglishMainPage />} />
            <Route exact path="/proNas" element={<ProNas />} />
          </Routes>
        </div>
      </animated.div>
    </div>
  ));
    }

export default App;
